import{_ as e,o as l,c as a,ag as d}from"./chunks/framework.C3MuDajo.js";const f=JSON.parse('{"title":"操作手册","description":"","frontmatter":{},"headers":[],"relativePath":"manual.md","filePath":"manual.md"}'),i={name:"manual.md"};function o(s,t,n,r,c,g){return l(),a("div",null,[...t[0]||(t[0]=[d(`<h1 id="操作手册" tabindex="-1">操作手册 <a class="header-anchor" href="#操作手册" aria-label="Permalink to &quot;操作手册&quot;">​</a></h1><p>本手册详细介绍 EICAS 仿真系统的操作方法。</p><hr><h2 id="🎮-驾驶舱控制" tabindex="-1">🎮 驾驶舱控制 <a class="header-anchor" href="#🎮-驾驶舱控制" aria-label="Permalink to &quot;🎮 驾驶舱控制&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">按钮</th><th style="text-align:left;">功能</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>START</strong></td><td style="text-align:left;">启动发动机</td><td style="text-align:left;">仅在 <code>ST_OFF</code> 状态且燃油充足时有效</td></tr><tr><td style="text-align:left;"><strong>STOP</strong></td><td style="text-align:left;">紧急停车</td><td style="text-align:left;"><strong>最高优先级</strong>，立即切断燃油并重置所有故障开关</td></tr><tr><td style="text-align:left;"><strong>THR +</strong></td><td style="text-align:left;">增加推力</td><td style="text-align:left;">仅在 <code>ST_RUN</code> 状态有效，燃油流速 +1，N1/EGT 增加 3%~5%</td></tr><tr><td style="text-align:left;"><strong>THR -</strong></td><td style="text-align:left;">减小推力</td><td style="text-align:left;">仅在 <code>ST_RUN</code> 状态有效，燃油流速 -1，N1/EGT 减少 3%~5%</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">操作优先级</p><p><strong>STOP 按钮</strong> 的优先级高于所有其他操作。按下后：</p><ol><li>立即进入 <code>ST_STOP</code> 状态</li><li>重置该发动机所有故障按钮</li><li>恢复所有传感器状态为正常</li></ol></div><hr><h2 id="🚨-告警色码系统" tabindex="-1">🚨 告警色码系统 <a class="header-anchor" href="#🚨-告警色码系统" aria-label="Permalink to &quot;🚨 告警色码系统&quot;">​</a></h2><p>系统采用三色告警体系（<code>AlertLevel</code> 枚举）：</p><table tabindex="0"><thead><tr><th style="text-align:left;">颜色</th><th style="text-align:left;">等级</th><th style="text-align:left;">代码</th><th style="text-align:left;">含义</th><th style="text-align:left;">触发条件示例</th></tr></thead><tbody><tr><td style="text-align:left;">⬜ <strong>白色</strong></td><td style="text-align:left;">STATUS</td><td style="text-align:left;"><code>LVL_WHITE</code></td><td style="text-align:left;">状态信息/轻微故障</td><td style="text-align:left;">单个传感器通道失效</td></tr><tr><td style="text-align:left;">🟨 <strong>琥珀色</strong></td><td style="text-align:left;">CAUTION</td><td style="text-align:left;"><code>LVL_AMBER</code></td><td style="text-align:left;">警戒，需要关注</td><td style="text-align:left;">N1 &gt; 105%、EGT 偏高、燃油低</td></tr><tr><td style="text-align:left;">🟥 <strong>红色</strong></td><td style="text-align:left;">WARNING</td><td style="text-align:left;"><code>LVL_RED</code></td><td style="text-align:left;">严重警告，需立即处理</td><td style="text-align:left;">N1 &gt; 120%、EGT 超温、双通道全失效</td></tr></tbody></table><h3 id="具体告警阈值" tabindex="-1">具体告警阈值 <a class="header-anchor" href="#具体告警阈值" aria-label="Permalink to &quot;具体告警阈值&quot;">​</a></h3><p><strong>N1 转速告警：</strong></p><ul><li><code>N1 &gt; 105%</code> → 琥珀色 <code>RPM HIGH</code></li><li><code>N1 &gt; 120%</code> → 红色 <code>OVERSPEED</code> + 自动停车</li></ul><p><strong>EGT 温度告警（启动阶段）：</strong></p><ul><li><code>EGT &gt; 850°C</code> → 琥珀色</li><li><code>EGT &gt; 1000°C</code> → 红色 <code>OVERHEAT</code> + 自动停车</li></ul><p><strong>EGT 温度告警（运行阶段）：</strong></p><ul><li><code>EGT &gt; 950°C</code> → 琥珀色</li><li><code>EGT &gt; 1100°C</code> → 红色 <code>OVERHEAT</code> + 自动停车</li></ul><p><strong>燃油系统告警：</strong></p><ul><li><code>燃油流速 &gt; 50</code> → 琥珀色 <code>FUEL FLOW HIGH</code></li><li><code>燃油余量 &lt; 1000</code> → 琥珀色 <code>FUEL QTY LOW</code></li><li><code>燃油耗尽</code> → 红色 <code>FUEL EXHAUSTED</code> + 双发停车</li></ul><hr><h2 id="📋-状态指示灯" tabindex="-1">📋 状态指示灯 <a class="header-anchor" href="#📋-状态指示灯" aria-label="Permalink to &quot;📋 状态指示灯&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">指示灯</th><th style="text-align:left;">亮起条件</th><th style="text-align:left;">熄灭条件</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>START</strong></td><td style="text-align:left;"><code>state == ST_START</code></td><td style="text-align:left;">进入其他状态</td></tr><tr><td style="text-align:left;"><strong>RUN</strong></td><td style="text-align:left;"><code>state == ST_RUN &amp;&amp; disp_N1 &gt;= 95.0</code></td><td style="text-align:left;">N1 &lt; 95% 或停止</td></tr></tbody></table><hr><h2 id="🔧-标准操作程序" tabindex="-1">🔧 标准操作程序 <a class="header-anchor" href="#🔧-标准操作程序" aria-label="Permalink to &quot;🔧 标准操作程序&quot;">​</a></h2><h3 id="正常启动" tabindex="-1">正常启动 <a class="header-anchor" href="#正常启动" aria-label="Permalink to &quot;正常启动&quot;">​</a></h3><ol><li>确认发动机处于 <code>ST_OFF</code> 状态</li><li>点击 <strong>START</strong> 按钮，START 灯亮起</li><li>前 2 秒：线性建立期（N1 和燃油流速线性增加）</li><li>2 秒后：对数爬升期（N1、EGT、燃油流速对数增长）</li><li>N1 达到 95% 后，自动切换到 <code>ST_RUN</code> 状态</li><li>START 灯熄灭，RUN 灯亮起</li></ol><h3 id="正常停车" tabindex="-1">正常停车 <a class="header-anchor" href="#正常停车" aria-label="Permalink to &quot;正常停车&quot;">​</a></h3><ol><li>点击 <strong>STOP</strong> 按钮</li><li>燃油流速立即归零</li><li>N1 和 EGT 按对数规律衰减（约 9 秒）</li><li>N1 &lt; 50 RPM 后，切换到 <code>ST_OFF</code> 状态</li></ol><h3 id="紧急情况处理" tabindex="-1">紧急情况处理 <a class="header-anchor" href="#紧急情况处理" aria-label="Permalink to &quot;紧急情况处理&quot;">​</a></h3><div class="danger custom-block"><p class="custom-block-title">自动停车触发条件</p><p>以下情况系统会自动执行停车：</p><ul><li>N1 &gt; 120%（严重超速）</li><li>EGT 超过红线温度</li><li>双发传感器全部失效</li><li>燃油耗尽</li></ul></div><hr><h2 id="📊-数据记录" tabindex="-1">📊 数据记录 <a class="header-anchor" href="#📊-数据记录" aria-label="Permalink to &quot;📊 数据记录&quot;">​</a></h2><p>系统自动生成两类文件：</p><table tabindex="0"><thead><tr><th style="text-align:left;">文件类型</th><th style="text-align:left;">文件名格式</th><th style="text-align:left;">内容</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>飞行数据</strong></td><td style="text-align:left;"><code>flight_data_YYYYMMDD_HHMMSS.csv</code></td><td style="text-align:left;">A/B 通道原始数据、融合显示值、燃油流速、燃油余量</td></tr><tr><td style="text-align:left;"><strong>错误日志</strong></td><td style="text-align:left;"><code>error_log.txt</code></td><td style="text-align:left;">时间戳 + 告警等级 + 告警信息</td></tr></tbody></table><h3 id="csv-数据列说明" tabindex="-1">CSV 数据列说明 <a class="header-anchor" href="#csv-数据列说明" aria-label="Permalink to &quot;CSV 数据列说明&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Time, L_N1_A, L_N1_B, L_N1_Disp, L_EGT_A, L_EGT_B, L_EGT_Disp, L_Flow,</span></span>
<span class="line"><span>      R_N1_A, R_N1_B, R_N1_Disp, R_EGT_A, R_EGT_B, R_EGT_Disp, R_Flow, Fuel_Qty</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,36)])])}const x=e(i,[["render",o]]);export{f as __pageData,x as default};
